          case "7days":
            suspensionExpiresAt = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000);
            break;
          case "30days":
            suspensionExpiresAt = new Date(now.getTime() + 30 * 24 * 60 * 60 * 1000);
