        // 2. Get polls from kliq members (optimized query)
        friendIds.length > 0 ? db
          .select({
            id: polls.id,
            userId: polls.userId,
            title: polls.title,
            description: polls.description,
            options: polls.options,
            expiresAt: polls.expiresAt,
