import { useState } from "react";
import type { ReactNode } from "react";
import Uppy from "@uppy/core";
import { DashboardModal } from "@uppy/react";
import "@uppy/core/dist/style.min.css";
import "@uppy/dashboard/dist/style.min.css";
import AwsS3 from "@uppy/aws-s3";
import type { UploadResult } from "@uppy/core";
import { Button } from "@/components/ui/button";

interface ObjectUploaderProps {
  maxNumberOfFiles?: number;
  maxFileSize?: number;
  allowedFileTypes?: string[];
  onGetUploadParameters: () => Promise<{
    method: "PUT";
    url: string;
  }>;
  onComplete?: (
    result: UploadResult<Record<string, unknown>, Record<string, unknown>>
  ) => void;
  buttonClassName?: string;
  children: ReactNode;
}

/**
 * A file upload component that renders as a button and provides a modal interface for
 * file management.
 * 
 * Features:
 * - Renders as a customizable button that opens a file upload modal
 * - Provides a modal interface for:
 *   - File selection
 *   - File preview
 *   - Upload progress tracking
 *   - Upload status display
 * 
 * The component uses Uppy under the hood to handle all file upload functionality.
 * All file management features are automatically handled by the Uppy dashboard modal.
 * 
 * @param props - Component props
 * @param props.maxNumberOfFiles - Maximum number of files allowed to be uploaded
 *   (default: 1)
 * @param props.maxFileSize - Maximum file size in bytes (default: 10MB)
 * @param props.onGetUploadParameters - Function to get upload parameters (method and URL).
 *   Typically used to fetch a presigned URL from the backend server for direct-to-S3
 *   uploads.
 * @param props.onComplete - Callback function called when upload is complete. Typically
 *   used to make post-upload API calls to update server state and set object ACL
 *   policies.
 * @param props.buttonClassName - Optional CSS class name for the button
 * @param props.children - Content to be rendered inside the button
 */
export function ObjectUploader({
  maxNumberOfFiles = 1,
  maxFileSize = 10485760, // 10MB default
  allowedFileTypes,
  onGetUploadParameters,
  onComplete,
  buttonClassName,
  children,
}: ObjectUploaderProps) {
  const [showModal, setShowModal] = useState(false);
  const [uppy] = useState(() =>
    new Uppy({
      restrictions: {
        maxNumberOfFiles,
        maxFileSize,
        allowedFileTypes: allowedFileTypes || ['*'], // Use provided types or allow all
      },
      autoProceed: false,
    })
      .use(AwsS3, {
        shouldUseMultipart: false,
        getUploadParameters: onGetUploadParameters,
      })
      .on("complete", (result) => {
        onComplete?.(result);
        setShowModal(false);
      })
  );

  return (
    <div>
      <Button onClick={() => setShowModal(true)} className={buttonClassName}>
        {children}
      </Button>

      <DashboardModal
        uppy={uppy}
        open={showModal}
        onRequestClose={() => setShowModal(false)}
        proudlyDisplayPoweredByUppy={false}
      />
    </div>
  );
}

client/src/components/PollCard.tsx.content:
import React, { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Clock, Users, Check } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { usePostTranslation } from "@/lib/translationService";

interface PollOption {
  option: string;
  index: number;
  votes: number;
  percentage: number;
}

interface Poll {
  id: string;
  title: string;
  description?: string;
  options: string[];
  expiresAt: string;
  isActive: boolean;
  createdAt: string;
  author: {
    id: string;
    firstName?: string;
    lastName?: string;
    profileImageUrl?: string;
  };
  votes: any[];
  totalVotes: number;
  userVote?: {
    selectedOption: number;
  };
}

interface PollCardProps {
  poll: Poll;
}

export function PollCard({ poll }: PollCardProps) {
  const [selectedOption, setSelectedOption] = useState<number | null>(
    poll.userVote?.selectedOption ?? null
  );
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { translatePost } = usePostTranslation();

  const { data: results = [], refetch: refetchResults } = useQuery<PollOption[]>({
    queryKey: ["/api/polls", poll.id, "results"],
    queryFn: () => apiRequest("GET", `/api/polls/${poll.id}/results`),
    refetchInterval: 3000, // Auto-refresh every 3 seconds for real-time updates
    staleTime: 0, // Always consider data stale to ensure fresh results
  });

  const voteMutation = useMutation({
    mutationFn: async (option: number) => {
      const response = await apiRequest("POST", `/api/polls/${poll.id}/vote`, { selectedOption: option });
      return { option, response };
    },
    onMutate: async (option: number) => {
      // Cancel any outgoing refetches
      await queryClient.cancelQueries({ queryKey: ["/api/polls", poll.id, "results"] });

      // Snapshot the previous value
      const previousResults = queryClient.getQueryData(["/api/polls", poll.id, "results"]);

      // Optimistically update UI immediately
      setSelectedOption(option);

      return { previousResults, option };
    },
    onSuccess: (data) => {
      // Use server response if available, otherwise refetch
      if (data.response?.results) {
        queryClient.setQueryData(["/api/polls", poll.id, "results"], data.response.results);
      } else {
        refetchResults();
      }

      // Invalidate related queries
      queryClient.invalidateQueries({ queryKey: ["/api/polls"] });
      queryClient.invalidateQueries({ queryKey: ["/api/kliq-feed"] });

      toast({
        title: "Vote recorded!",
        description: "Your vote has been saved successfully",
      });
    },
    onError: (error, option, context) => {
      // If the mutation fails, use the context returned from onMutate to roll back
      setSelectedOption(null);
      if (context?.previousResults) {
        queryClient.setQueryData(["/api/polls", poll.id, "results"], context.previousResults);
      }
      console.error("Error voting:", error);
      toast({
        title: "Failed to vote",
        description: "Please try again",
        variant: "destructive",
      });
    },
  });

  const handleVote = (optionIndex: number) => {
    setSelectedOption(optionIndex);
    voteMutation.mutate(optionIndex);
  };

  const isExpired = new Date(poll.expiresAt) <= new Date();
  const hasVoted = poll.userVote !== undefined || selectedOption !== null;
  const totalVotes = results.reduce((sum, result) => sum + (result?.votes || 0), 0);

  // Always show results when there are votes, regardless of user's vote status  
  const showResults = totalVotes > 0 || hasVoted || isExpired;

  return (
    <Card className="bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700" data-testid={`card-poll-${poll.id}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="text-lg text-black dark:text-white">{translatePost(poll.title)}</CardTitle>
            {poll.description && (
              <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
                {translatePost(poll.description)}
              </p>
            )}
          </div>
          <div className="flex items-center gap-2">
            <Badge 
              variant={isExpired ? "destructive" : "secondary"} 
              className="text-xs"
              data-testid={`badge-poll-status-${poll.id}`}
            >
              {isExpired ? "Expired" : "Active"}
            </Badge>
          </div>
        </div>
        <div className="flex items-center gap-4 text-xs text-gray-500 dark:text-gray-400">
          <div className="flex items-center gap-1">
            <img
              src={poll.author.profileImageUrl || "/default-avatar.png"}
              alt={`${poll.author.firstName} ${poll.author.lastName}`}
              className="w-4 h-4 rounded-full"
            />
            <span>{poll.author.firstName} {poll.author.lastName}</span>
          </div>
          <div className="flex items-center gap-1">
            <Clock className="w-3 h-3" />
            <span>
              {isExpired 
                ? `Expired ${formatDistanceToNow(new Date(poll.expiresAt))} ago`
                : `Expires ${formatDistanceToNow(new Date(poll.expiresAt))}`
              }
            </span>
          </div>
          <div className="flex items-center gap-1">
            <Users className="w-3 h-3" />
            <span>{totalVotes} votes</span>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-3">
          {poll.options.map((option, index) => {
            const result = (results as PollOption[]).find((r: PollOption) => r.index === index);
            const isSelected = selectedOption === index;

            return (
              <div key={index} className="space-y-2">
                <div className="flex items-center justify-between">
                  <Button
                    variant={isSelected && hasVoted ? "default" : "outline"}
                    className={`flex-1 justify-start text-left h-auto p-3 ${
                      showResults ? "cursor-default" : ""
                    } bg-white dark:bg-gray-700 text-black dark:text-white border-gray-300 dark:border-gray-600`}
                    onClick={() => !hasVoted && !isExpired && handleVote(index)}
                    disabled={hasVoted || isExpired || voteMutation.isPending}
                    data-testid={`button-poll-option-${poll.id}-${index}`}
                  >
                    <div className="flex items-center gap-2 w-full">
                      {isSelected && hasVoted && <Check className="w-4 h-4 text-green-600" />}
                      <span className="flex-1">{translatePost(option)}</span>
                      {showResults && (
                        <div className="flex items-center gap-2">
                          <span className="text-sm font-medium">
                            {result?.percentage || 0}%
                          </span>
                          <span className="text-xs text-gray-500">
                            ({result?.votes || 0})
                          </span>
                        </div>
                      )}
                    </div>
                  </Button>
                </div>
                {showResults && (
                  <Progress 
                    value={result?.percentage || 0} 
                    className="h-2"
                    data-testid={`progress-poll-option-${poll.id}-${index}`}
                  />
                )}
              </div>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ProfileDetailsDisp.content:
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Heart, MapPin, Utensils, Music, Users, BookOpen, Film, Gamepad2, PawPrint } from "lucide-react";
import { usePostTranslation } from "@/lib/translationService";

interface ProfileDetailsDisplayProps {
  user: any;
}

export function ProfileDetailsDisplay({ user }: ProfileDetailsDisplayProps) {
  const { translatePost } = usePostTranslation();
  if (!user) return null;

  const hasAnyDetails = 
    user.interests?.length > 0 ||
    user.favoriteLocations?.length > 0 ||
    user.favoriteFoods?.length > 0 ||
    user.musicGenres?.length > 0 ||
    user.hobbies?.length > 0 ||
    user.favoriteMovies?.length > 0 ||
    user.favoriteBooks?.length > 0 ||
    user.relationshipStatus ||
    user.petPreferences ||
    user.lifestyle;

  if (!hasAnyDetails) {
    return (
      <Card className="bg-card/50 border-border">
        <CardContent className="p-6 text-center">
          <p className="text-muted-foreground">No profile details added yet</p>
        </CardContent>
      </Card>
    );
  }

  const DetailSection = ({ 
    title, 
    items, 
    icon: Icon, 
    singleValue 
  }: { 
    title: string; 
    items?: string[]; 
    icon: any; 
    singleValue?: string;
  }) => {
    if (singleValue) {
      return (
        <div className="space-y-2">
          <div className="flex items-center gap-2 text-primary font-medium">
            <Icon className="w-4 h-4" />
            {title}
          </div>
          <div className="text-foreground">{translatePost(singleValue)}</div>
        </div>
      );
    }

    if (!items || items.length === 0) return null;

    return (
      <div className="space-y-2">
        <div className="flex items-center gap-2 text-primary font-medium">
          <Icon className="w-4 h-4" />
          {title}
        </div>
        <div className="flex flex-wrap gap-2">
          {items.map((item, index) => (
            <Badge
              key={index}
              variant="secondary"
              className="bg-secondary text-secondary-foreground hover:bg-secondary/80"
            >
              {translatePost(item)}
            </Badge>
          ))}
        </div>
      </div>
    );
  };

  const formatRelationshipStatus = (status: string) => {
    switch (status) {
      case "single": return "Single";
      case "taken": return "In a relationship";
      case "married": return "Married";
      case "complicated": return "It's complicated";
      case "prefer-not-to-say": return "Prefer not to say";
      default: return status;
    }
  };

  const formatPetPreferences = (pref: string) => {
    switch (pref) {
      case "dogs": return "Dog lover";
      case "cats": return "Cat lover";
      case "both": return "Loves dogs & cats";
      case "other": return "Other pets";
      case "none": return "No pets";
      default: return pref;
    }
  };

  const formatLifestyle = (lifestyle: string) => {
    return lifestyle.charAt(0).toUpperCase() + lifestyle.slice(1);
  };

  return (
    <div className="space-y-4">
      {/* Interests & Hobbies */}
      {(user.interests?.length > 0 || user.hobbies?.length > 0) && (
        <Card className="bg-card/50 border-border">
          <CardHeader>
            <CardTitle className="text-primary flex items-center gap-2">
              <Heart className="w-5 h-5" />
              {translatePost("Interests & Hobbies")}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <DetailSection title={translatePost("Interests")} items={user.interests} icon={Heart} />
            <DetailSection title={translatePost("Hobbies")} items={user.hobbies} icon={Gamepad2} />
          </CardContent>
        </Card>
      )}

      {/* Favorites */}
      {(user.favoriteLocations?.length > 0 || user.favoriteFoods?.length > 0 || user.musicGenres?.length > 0) && (
        <Card className="bg-card/50 border-border">
          <CardHeader>
            <CardTitle className="text-primary flex items-center gap-2">
              <MapPin className="w-5 h-5" />
              {translatePost("Favorites")}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <DetailSection title={translatePost("Places")} items={user.favoriteLocations} icon={MapPin} />
            <DetailSection title={translatePost("Foods")} items={user.favoriteFoods} icon={Utensils} />
            <DetailSection title={translatePost("Music Genres")} items={user.musicGenres} icon={Music} />
          </CardContent>
        </Card>
      )}

      {/* Lifestyle & Status */}
      {(user.relationshipStatus || user.petPreferences || user.lifestyle) && (
        <Card className="bg-card/50 border-border">
          <CardHeader>
            <CardTitle className="text-primary flex items-center gap-2">
              <Users className="w-5 h-5" />
              {translatePost("Lifestyle & Status")}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {user.relationshipStatus && (
              <DetailSection 
                title={translatePost("Relationship Status")} 
                singleValue={translatePost(formatRelationshipStatus(user.relationshipStatus))} 
                icon={Heart} 
              />
            )}
            {user.petPreferences && (
              <DetailSection 
                title={translatePost("Pet Preferences")} 
                singleValue={translatePost(formatPetPreferences(user.petPreferences))} 
                icon={PawPrint} 
              />
            )}
            {user.lifestyle && (
              <DetailSection 
                title={translatePost("Lifestyle")} 
                singleValue={translatePost(formatLifestyle(user.lifestyle))} 
                icon={Users} 
              />
            )}
          </CardContent>
        </Card>
      )}

      {/* Entertainment */}
      {(user.favoriteMovies?.length > 0 || user.favoriteBooks?.length > 0) && (
        <Card className="bg-card/50 border-border">
          <CardHeader>
            <CardTitle className="text-primary flex items-center gap-2">
              <Film className="w-5 h-5" />
              {translatePost("Entertainment")}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <DetailSection title={translatePost("Movies")} items={user.favoriteMovies} icon={Film} />
            <DetailSection title={translatePost("Books")} items={user.favoriteBooks} icon={BookOpen} />
          </CardContent>
        </Card>
      )}
    </div>
  );
}

