import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { X, Plus, Shield } from "lucide-react";
import type { ContentFilter } from "@shared/schema";

interface FilterManagerProps {
  filters: ContentFilter[];
  onAddFilter: (keyword: string) => void;
  onRemoveFilter: (filterId: string) => void;
}

export function FilterManager({ filters, onAddFilter, onRemoveFilter }: FilterManagerProps) {
  const [newKeyword, setNewKeyword] = useState("");

  const handleAddFilter = () => {
    if (newKeyword.trim()) {
      onAddFilter(newKeyword.trim().toLowerCase());
      setNewKeyword("");
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      handleAddFilter();
    }
  };

  return (
    <Card className="bg-card border-border">
      <CardHeader>
        <CardTitle className="text-primary flex items-center gap-2">
          <Shield className="w-5 h-5" />
          üö´ Content Filters
        </CardTitle>
        <p className="text-sm text-muted-foreground">
          Hide posts containing these keywords from your feed
        </p>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Add Filter Input */}
        <div className="flex gap-2">
          <Input
            value={newKeyword}
            onChange={(e) => setNewKeyword(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Enter keyword to filter..."
            className="flex-1 bg-input border-border text-foreground placeholder-muted-foreground"
          />
          <Button
            onClick={handleAddFilter}
            disabled={!newKeyword.trim()}
            className="bg-primary hover:bg-primary/90 text-primary-foreground"
          >
            <Plus className="w-4 h-4" />
          </Button>
        </div>

        {/* Current Filters */}
        <div className="space-y-2">
          <h4 className="text-sm font-medium text-foreground">
            Active Filters ({filters.length})
          </h4>
          {filters.length === 0 ? (
            <p className="text-sm text-muted-foreground italic">
              No filters set. All posts will be visible.
            </p>
          ) : (
            <div className="flex flex-wrap gap-2">
              {filters.map((filter) => (
                <Badge
                  key={filter.id}
                  variant="destructive"
                  className="bg-destructive text-destructive-foreground hover:bg-destructive/90 cursor-pointer group"
                  onClick={() => onRemoveFilter(filter.id)}
                >
                  {filter.keyword}
                  <X className="w-3 h-3 ml-1 group-hover:scale-110 transition-transform" />
                </Badge>
              ))}
            </div>
          )}
        </div>

        {/* Info Box */}
        <div className="bg-muted/30 border border-border rounded-lg p-3">
          <p className="text-xs text-muted-foreground">
            üí° <strong>Tip:</strong> Filters are case-insensitive and will hide any post 
            containing these keywords. Add filters for topics you'd rather not see in your feed.
          </p>
        </div>

        {/* Common Filter Suggestions */}
        <div className="space-y-2">
          <h4 className="text-sm font-medium text-foreground">
            Common Filters
          </h4>
          <div className="flex flex-wrap gap-2">
            {["drama", "politics", "negative", "spam", "toxic", "hate"].map((suggestion) => {
              const alreadyFiltered = filters.some(f => f.keyword === suggestion);
              return (
                <Button
                  key={suggestion}
                  variant="outline"
                  size="sm"
                  onClick={() => !alreadyFiltered && onAddFilter(suggestion)}
                  disabled={alreadyFiltered}
                  className={`text-xs ${alreadyFiltered 
                    ? 'bg-muted text-muted-foreground cursor-not-allowed' 
                    : 'bg-secondary text-secondary-foreground hover:bg-secondary/80 border-border'
                  }`}
                >
                  {alreadyFiltered ? '‚úì ' : '+ '}{suggestion}
                </Button>
              );
            })}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/pyramid-chart.tsx.content:
import { useState, useRef, useEffect } from "react";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { MessageCircle, Phone, Users, X } from "lucide-react";
import { cn } from "@/lib/utils";
import { useLocation } from "wouter";

interface Friend {
  id: string;
  firstName?: string;
  lastName?: string;
  profileImageUrl?: string;
  rank: number;
}

interface PyramidChartProps {
  friends: Friend[];
  onRankChange?: (friendId: string, newRank: number) => void;
  onMessage?: (friendId: string, friendName: string) => void;
  onVideoCall?: (participantIds: string[]) => void;
  onRemove?: (friendId: string) => void;
  maxFriends?: number;
  kliqName?: string;
  kliqLeftEmoji?: string;
  kliqRightEmoji?: string;
  kliqClosed?: boolean;
  onCloseKliq?: () => void;
  isClosingKliq?: boolean;
}

export function PyramidChart({ friends, onRankChange, onMessage, onVideoCall, onRemove, maxFriends = 28, kliqName, kliqLeftEmoji = 'üèÜ', kliqRightEmoji = 'üèÜ', kliqClosed, onCloseKliq, isClosingKliq }: PyramidChartProps) {
  const [draggedFriend, setDraggedFriend] = useState<Friend | null>(null);
  const [showRemoveButton, setShowRemoveButton] = useState<string | null>(null);
  const [isHolding, setIsHolding] = useState<string | null>(null);
  const [_, setLocation] = useLocation();
  const holdTimer = useRef<NodeJS.Timeout | null>(null);
  const vibrateTimer = useRef<NodeJS.Timeout | null>(null);

  // Cleanup timers on unmount
  useEffect(() => {
    return () => {
      clearTimeout(holdTimer.current!);
      clearInterval(vibrateTimer.current!);
    };
  }, []);

  // Handle click outside to cancel remove action
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      // Check if we're in remove mode
      if (showRemoveButton) {
        const target = event.target as Element;
        // Check if the click is outside any friend avatar or remove button
        const isClickOnFriend = target.closest('[data-testid^="friend-avatar-"]');
        const isClickOnRemoveButton = target.closest('[data-testid^="button-remove-"]');

        if (!isClickOnFriend && !isClickOnRemoveButton) {
          // Cancel the remove action
          setShowRemoveButton(null);
          setIsHolding(null);
          clearTimeout(holdTimer.current!);
          clearInterval(vibrateTimer.current!);

          // Stop vibration if it was running
          if (navigator.vibrate) {
            navigator.vibrate(0);
          }
        }
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showRemoveButton]);

  const getInitials = (friend: Friend) => {
    const first = friend.firstName?.[0] || "";
    const last = friend.lastName?.[0] || "";
    return first + last || "?";
  };

  const getName = (friend: Friend) => {
    const first = friend.firstName || "";
    const last = friend.lastName ? ` ${friend.lastName[0]}.` : "";
    return first + last || "Unknown";
  };

  // Sort friends by rank for pyramid layout
  const sortedFriends = [...friends].sort((a, b) => a.rank - b.rank);

  // Organize friends into pyramid rows: 1, 2, 3, 4, 5, 6, 7 (total: 28)
  const pyramidRows = [
    sortedFriends.slice(0, 1),    // Top: 1 friend (rank 1)
    sortedFriends.slice(1, 3),    // Second: 2 friends (ranks 2-3)
    sortedFriends.slice(3, 6),    // Third: 3 friends (ranks 4-6)
    sortedFriends.slice(6, 10),   // Fourth: 4 friends (ranks 7-10)
    sortedFriends.slice(10, 15),  // Fifth: 5 friends (ranks 11-15)
    sortedFriends.slice(15, 21),  // Sixth: 6 friends (ranks 16-21)
    sortedFriends.slice(21, 28)   // Bottom: 7 friends (ranks 22-28)
  ].filter(row => row.length > 0);

  const handleDragStart = (friend: Friend) => {
    setDraggedFriend(friend);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrop = (targetFriend: Friend) => {
    if (draggedFriend && onRankChange && draggedFriend.id !== targetFriend.id) {
      const targetRank = targetFriend.rank;
      console.log(`Moving ${draggedFriend.id} (rank ${draggedFriend.rank}) to position of ${targetFriend.id} (rank ${targetRank})`);
      onRankChange(draggedFriend.id, targetRank);
    }
    setDraggedFriend(null);
  };

  const handleHoldStart = (friend: Friend) => {
    if (!onRemove) return;

    clearTimeout(holdTimer.current!);
    clearInterval(vibrateTimer.current!);

    setIsHolding(friend.id);

    holdTimer.current = setTimeout(() => {
      setShowRemoveButton(friend.id);

      // Trigger vibration if supported
      if (navigator.vibrate) {
        navigator.vibrate(200); // Initial vibration
      }

      // Start continuous vibration every 500ms
      vibrateTimer.current = setInterval(() => {
        if (navigator.vibrate) {
          navigator.vibrate(100);
        }
      }, 500);
    }, 800); // Hold for 800ms to show remove button
  };

  const handleHoldEnd = () => {
    clearTimeout(holdTimer.current!);
    clearInterval(vibrateTimer.current!);
    setIsHolding(null);

    // Stop vibration if it was running
    if (navigator.vibrate) {
      navigator.vibrate(0);
    }
  };

  const handleRemoveClick = (friendId: string, e: React.MouseEvent) => {
    e.stopPropagation();
    if (onRemove) {
      onRemove(friendId);
    }
    setShowRemoveButton(null);
    setIsHolding(null);
    clearInterval(vibrateTimer.current!);
  };

  const handleAvatarClick = (friend: Friend, e: React.MouseEvent) => {
    e.stopPropagation();
    // Don't navigate if we're in holding/remove mode
    if (isHolding === friend.id || showRemoveButton === friend.id) {
      return;
    }
    setLocation(`/user/${friend.id}`);
  };

  const renderFriend = (friend: Friend) => (
    <div
      key={friend.id}
      draggable
      onDragStart={() => handleDragStart(friend)}
      onDragOver={handleDragOver}
      onDrop={() => handleDrop(friend)}
      className={cn(
        "relative group cursor-move transition-all duration-300 hover:scale-105 flex flex-col items-center",
        draggedFriend?.id === friend.id && "opacity-50"
      )}
    >
      {/* Action buttons - appear on hover */}
      <div className="absolute -top-2 -right-2 flex gap-1 z-20 opacity-0 group-hover:opacity-100 transition-opacity">
        {onVideoCall && (
          <Button
            size="sm"
            variant="ghost"
            onClick={(e) => {
              e.stopPropagation();
              onVideoCall([friend.id]);
            }}
            className="w-8 h-8 p-0 bg-red-500 hover:bg-red-600 text-white rounded-full shadow-lg"
            data-testid={`button-video-call-${friend.id}`}
          >
            <Phone className="w-4 h-4" />
          </Button>
        )}
        {onMessage && (
          <Button
            size="sm"
            variant="ghost"
            onClick={(e) => {
              e.stopPropagation();
              onMessage(friend.id, getName(friend));
            }}
            className="w-8 h-8 p-0 bg-primary hover:bg-primary/90 text-primary-foreground rounded-full shadow-lg"
            data-testid={`button-message-${friend.id}`}
          >
            <MessageCircle className="w-4 h-4" />
          </Button>
        )}
      </div>

      {/* Remove button - appears on hold */}
      {showRemoveButton === friend.id && onRemove && (
        <div className="absolute -top-2 -left-2 z-30">
          <Button
            size="sm"
            variant="ghost"
            onClick={(e) => handleRemoveClick(friend.id, e)}
            className="w-8 h-8 p-0 bg-destructive hover:bg-destructive/90 text-destructive-foreground rounded-full shadow-lg animate-in zoom-in-95 duration-200"
            data-testid={`button-remove-${friend.id}`}
          >
            <X className="w-4 h-4" />
          </Button>
        </div>
      )}

      <div 
        className={cn(
          "p-2 rounded-full bg-gradient-to-r from-primary to-secondary cursor-pointer shadow-lg hover:shadow-xl transition-all duration-200",
          isHolding === friend.id && "animate-pulse scale-110",
          showRemoveButton === friend.id && "animate-bounce"
        )}
        onClick={(e) => handleAvatarClick(friend, e)}
        onMouseDown={() => handleHoldStart(friend)}
        onMouseUp={handleHoldEnd}
        onMouseLeave={handleHoldEnd}
        onTouchStart={() => handleHoldStart(friend)}
        onTouchEnd={handleHoldEnd}
        data-testid={`friend-avatar-${friend.id}`}
      >
        <Avatar className="w-20 h-20 border-4 border-background">
          <AvatarImage src={friend.profileImageUrl} />
          <AvatarFallback className="bg-muted text-muted-foreground text-lg font-bold">
            {getInitials(friend)}
          </AvatarFallback>
        </Avatar>
      </div>
      <div className="text-center mt-3">
        <p className="text-sm font-medium text-foreground truncate max-w-[100px]">
          {getName(friend)}
        </p>
      </div>
    </div>
  );

  return (
    <div className="bg-card border border-border rounded-xl p-6 space-y-6">
      <div className="text-center">
        <h3 className="text-xl font-bold text-primary mb-2">
          {kliqLeftEmoji} {kliqName || "My Kliq"} {kliqRightEmoji}
        </h3>
        <p className="text-muted-foreground text-sm mb-4">
          Your friendship pyramid
        </p>
        <Badge variant="outline" className="border-primary text-primary">
          {friends.length}/{maxFriends} Friends
        </Badge>
      </div>

      {/* Pyramid Layout */}
      <div className="space-y-6">
        {pyramidRows.map((rowFriends, rowIndex) => (
          <div key={rowIndex} className="flex justify-center">
            <div className={cn(
              "flex gap-4 justify-center items-end",
              rowIndex === 0 && "gap-0", // Top friend gets special spacing
              rowIndex === 1 && "gap-6",
              rowIndex >= 2 && "gap-4"
            )}>
              {rowFriends.map(renderFriend)}
            </div>
          </div>
        ))}
      </div>

      {/* Bottom section with invite and close kliq buttons */}
      <div className="flex justify-between items-center mt-6">
        {/* Invite Friend Display - left side or center if no friends */}
        {friends.length < maxFriends && (
          <div className="bg-gradient-to-r from-primary to-secondary text-primary-foreground font-bold px-4 py-2 rounded-md flex items-center">
            <Users className="w-4 h-4 mr-2" />
            Invite Friend ({maxFriends - friends.length} spots left)
          </div>
        )}

        {/* Spacer when no invite button */}
        {friends.length >= maxFriends && <div />}

        {/* Open/Close Kliq Button - right side (only show if user has friends) */}
        {friends.length > 0 && onCloseKliq && (
          <Button 
            variant={kliqClosed ? "default" : "outline"} 
            size="sm"
            onClick={onCloseKliq}
            disabled={isClosingKliq}
            className={kliqClosed 
              ? "bg-green-600 hover:bg-green-700 text-white" 
              : "border-orange-500 text-orange-500 hover:bg-orange-500 hover:text-white"
            }
            data-testid="button-open-close-kliq"
          >
            {kliqClosed ? (
              <>
                <Users className="w-4 h-4 mr-2" />
                {isClosingKliq ? "Opening..." : "Open Kliq"}
              </>
            ) : (
              <>
                <X className="w-4 h-4 mr-2" />
                {isClosingKliq ? "Closing..." : "Close Kliq"}
              </>
            )}
          </Button>
        )}
      </div>
    </div>
  );
}

client/src/components/video-call.tsx.content:
import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Phone, 
  PhoneOff, 
  Mic, 
  MicOff, 
  Video, 
  VideoOff,
  Volume2,
  VolumeX,
  Users
} from "lucide-react";
import { cn } from "@/lib/utils";
import { useAuth } from "@/hooks/useAuth";
import { type User, type VideoCall, type CallParticipant } from "@shared/schema";

interface VideoCallProps {
  call: VideoCall & { participants: (CallParticipant & { user: User })[] };
  onEndCall: () => void;
  onToggleAudio: (enabled: boolean) => void;
  onToggleVideo: (enabled: boolean) => void;
}

export function VideoCallComponent({ call, onEndCall, onToggleAudio, onToggleVideo }: VideoCallProps) {
  const { user } = useAuth();
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const [isMuted, setIsMuted] = useState(false);
  const localVideoRef = useRef<HTMLVideoElement>(null);
  const remoteVideoRefs = useRef<{ [key: string]: HTMLVideoElement | null }>({});

  // WebRTC setup would go here in a real implementation
  useEffect(() => {
    // Initialize local video stream
    const initializeMedia = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ 
          video: isVideoEnabled, 
          audio: isAudioEnabled 
        });

        if (localVideoRef.current) {
          localVideoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error('Error accessing media devices:', error);
      }
    };

    initializeMedia();
  }, [isVideoEnabled, isAudioEnabled]);

  const handleToggleAudio = () => {
    const newState = !isAudioEnabled;
    setIsAudioEnabled(newState);
    onToggleAudio(newState);
  };

  const handleToggleVideo = () => {
    const newState = !isVideoEnabled;
    setIsVideoEnabled(newState);
    onToggleVideo(newState);
  };

  const handleToggleMute = () => {
    setIsMuted(!isMuted);
  };

  const otherParticipants = call.participants.filter(p => p.userId !== (user as User)?.id);

  return (
    <Card className="w-full h-full bg-card text-foreground">
      <CardHeader className="pb-2">
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg flex items-center gap-2">
            <Users className="w-5 h-5" />
            Group Call ({call.participants.length} participants)
          </CardTitle>
          <Badge variant={call.status === 'active' ? 'default' : 'secondary'}>
            {call.status}
          </Badge>
        </div>
      </CardHeader>

      <CardContent className="p-4">
        {/* Video Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-4">
          {/* Local video */}
          <div className="relative bg-muted rounded-lg overflow-hidden aspect-video">
            <video 
              ref={localVideoRef}
              autoPlay 
              muted 
              playsInline
              className={cn(
                "w-full h-full object-cover",
                !isVideoEnabled && "hidden"
              )}
              data-testid="video-local"
            />
            {!isVideoEnabled && (
              <div className="flex items-center justify-center w-full h-full bg-muted/50">
                <div className="text-center">
                  <div className="w-16 h-16 bg-primary rounded-full flex items-center justify-center mx-auto mb-2">
                    <span className="text-lg font-bold text-primary-foreground">
                      {(user as User)?.firstName?.[0]}
                    </span>
                  </div>
                  <p className="text-sm text-foreground">{(user as User)?.firstName} (You)</p>
                </div>
              </div>
            )}
            <div className="absolute bottom-2 left-2 bg-black bg-opacity-50 px-2 py-1 rounded text-xs">
              You
            </div>
          </div>

          {/* Remote participants */}
          {otherParticipants.map((participant) => (
            <div key={participant.userId} className="relative bg-muted rounded-lg overflow-hidden aspect-video">
              <video
                ref={(el) => {
                  remoteVideoRefs.current[participant.userId] = el;
                }}
                autoPlay
                playsInline
                className="w-full h-full object-cover"
                data-testid={`video-participant-${participant.userId}`}
              />
              <div className="flex items-center justify-center w-full h-full bg-muted/50">
                <div className="text-center">
                  <div className="w-16 h-16 bg-secondary rounded-full flex items-center justify-center mx-auto mb-2">
                    <span className="text-lg font-bold text-secondary-foreground">
                      {participant.user.firstName?.[0]}
                    </span>
                  </div>
                  <p className="text-sm text-foreground">{participant.user.firstName}</p>
                </div>
              </div>
              <div className="absolute bottom-2 left-2 bg-black bg-opacity-50 px-2 py-1 rounded text-xs">
                {participant.user.firstName}
              </div>
              <Badge 
                className="absolute top-2 right-2"
                variant={participant.status === 'joined' ? 'default' : 'secondary'}
              >
                {participant.status}
              </Badge>
            </div>
          ))}
        </div>

        {/* Call Controls */}
        <div className="flex justify-center gap-4">
          <Button
            variant={isAudioEnabled ? "default" : "destructive"}
            size="lg"
            onClick={handleToggleAudio}
            className="rounded-full w-12 h-12 p-0"
            data-testid="button-toggle-audio"
          >
            {isAudioEnabled ? <Mic className="w-5 h-5" /> : <MicOff className="w-5 h-5" />}
          </Button>

          <Button
            variant={isVideoEnabled ? "default" : "destructive"}
            size="lg"
            onClick={handleToggleVideo}
            className="rounded-full w-12 h-12 p-0"
            data-testid="button-toggle-video"
          >
            {isVideoEnabled ? <Video className="w-5 h-5" /> : <VideoOff className="w-5 h-5" />}
          </Button>

          <Button
            variant={isMuted ? "destructive" : "default"}
            size="lg"
            onClick={handleToggleMute}
            className="rounded-full w-12 h-12 p-0"
            data-testid="button-toggle-mute"
          >
            {isMuted ? <VolumeX className="w-5 h-5" /> : <Volume2 className="w-5 h-5" />}
          </Button>

          <Button
            variant="destructive"
            size="lg"
            onClick={onEndCall}
            className="rounded-full w-12 h-12 p-0"
            data-testid="button-end-call"
          >
            <PhoneOff className="w-5 h-5" />
          </Button>
        </div>

        {/* Participants List */}
        <div className="mt-4 space-y-2">
          <h4 className="text-sm font-medium">Participants ({call.participants.length})</h4>
          <div className="grid grid-cols-2 gap-2">
            {call.participants.map((participant) => (
              <div key={participant.userId} className="flex items-center gap-2 p-2 bg-gray-800 rounded">
                <div className="w-8 h-8 bg-pink-500 rounded-full flex items-center justify-center text-xs">
                  {participant.user.firstName?.[0]}
                </div>
                <span className="text-sm truncate flex-1">
                  {participant.user.firstName}
                  {participant.userId === (user as User)?.id && " (You)"}
                </span>
                <Badge variant={participant.status === 'joined' ? 'default' : 'secondary'}>
                  {participant.status}
                </Badge>
              </div>
            ))}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

