import React, { useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import type { UserTheme } from "@shared/schema";

// Helper function to convert hex to HSL
const hexToHsl = (hex: string) => {
  const r = parseInt(hex.slice(1, 3), 16) / 255;
  const g = parseInt(hex.slice(3, 5), 16) / 255;
  const b = parseInt(hex.slice(5, 7), 16) / 255;

  const max = Math.max(r, g, b);
  const min = Math.min(r, g, b);
  let h, s, l = (max + min) / 2;

  if (max === min) {
    h = s = 0;
  } else {
    const d = max - min;
    s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
    switch (max) {
      case r: h = (g - b) / d + (g < b ? 6 : 0); break;
      case g: h = (b - r) / d + 2; break;
      case b: h = (r - g) / d + 4; break;
      default: h = 0;
    }
    h /= 6;
  }

  return `${Math.round(h * 360)}, ${Math.round(s * 100)}%, ${Math.round(l * 100)}%`;
};

// Apply theme to document root
const applyTheme = (theme: Partial<UserTheme>) => {
  const root = document.documentElement;

  if (theme.primaryColor) {
    root.style.setProperty('--primary', `hsl(${hexToHsl(theme.primaryColor)})`);
    root.style.setProperty('--mykliq-pink', `hsl(${hexToHsl(theme.primaryColor)})`);
  }

  if (theme.secondaryColor) {
    root.style.setProperty('--secondary', `hsl(${hexToHsl(theme.secondaryColor)})`);
    root.style.setProperty('--mykliq-blue', `hsl(${hexToHsl(theme.secondaryColor)})`);
  }

  if (theme.fontFamily) {
    const fontMap = {
      comic: 'Comic Neue, Comic Sans MS, cursive',
      retro: 'Courier Prime, Courier New, monospace',
      helvetica: 'Helvetica, sans-serif',
      times: 'Times New Roman, serif',
      impact: 'Impact, sans-serif'
    };
    root.style.setProperty('--font-sans', fontMap[theme.fontFamily as keyof typeof fontMap] || fontMap.comic);
  }

  // Apply background customization
  if (theme.backgroundType && theme.backgroundType === 'solid' && theme.backgroundColor) {
    root.style.setProperty('--background', `hsl(${hexToHsl(theme.backgroundColor)})`);
    root.style.removeProperty('background-image');
  } else if (theme.backgroundType === 'gradient' && theme.backgroundGradientStart && theme.backgroundGradientEnd) {
    const startHsl = hexToHsl(theme.backgroundGradientStart);
    const endHsl = hexToHsl(theme.backgroundGradientEnd);
    root.style.setProperty('--background', `linear-gradient(135deg, hsl(${startHsl}), hsl(${endHsl}))`);
    root.style.setProperty('background-image', `linear-gradient(135deg, hsl(${startHsl}), hsl(${endHsl}))`);
  } else if (theme.backgroundType === 'pattern' && theme.backgroundPattern) {
    // Apply pattern backgrounds
    const patterns = {
      dots: `radial-gradient(circle, hsl(${theme.primaryColor ? hexToHsl(theme.primaryColor) : '328, 100%, 54%'}) 1px, transparent 1px)`,
      lines: `linear-gradient(45deg, hsl(${theme.primaryColor ? hexToHsl(theme.primaryColor) : '328, 100%, 54%'}) 1px, transparent 1px)`,
      waves: `url("data:image/svg+xml,%3Csvg width='40' height='40' viewBox='0 0 40 40' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M20 20c0-11 9-20 20-20s20 9 20 20-9 20-20 20-20-9-20-20z' fill='%23${theme.primaryColor?.replace('#', '') || 'FF1493'}' opacity='0.1'/%3E%3C/svg%3E")`,
      geometric: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23${theme.primaryColor?.replace('#', '') || 'FF1493'}' opacity='0.1'%3E%3Cpath d='M30 30l15-15v30z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
    };
    root.style.setProperty('background-image', patterns[theme.backgroundPattern as keyof typeof patterns] || patterns.dots);
    root.style.setProperty('background-size', theme.backgroundPattern === 'dots' ? '20px 20px' : '40px 40px');
  }
};

export function useTheme() {
  const { data: theme, isLoading } = useQuery({
    queryKey: ["/api/user/theme"],
    enabled: true, // Re-enabled with proper caching
    retry: false,
    staleTime: Infinity, // Never consider data stale
    gcTime: Infinity, // Never garbage collect
    refetchOnWindowFocus: false, // Don't refetch when window gains focus
    refetchOnMount: false, // Don't refetch on component mount if data exists
    refetchInterval: false, // Disable automatic refetching
    refetchOnReconnect: false, // Don't refetch on reconnect
  });

  // Apply theme when it loads or changes
  useEffect(() => {
    if (theme) {
      applyTheme(theme);
    }
  }, [theme]);

  return {
    theme,
    isLoading,
    applyTheme,
  };
}

client/src/hooks/useVideoCall.ts.content:
import { useState, useEffect, useRef } from "react";
import { useAuth } from "./useAuth";
import { apiRequest } from "@/lib/queryClient";
import { type VideoCall, type CallParticipant, type User } from "@shared/schema";

interface VideoCallState {
  currentCall: (VideoCall & { participants: (CallParticipant & { user: User })[] }) | null;
  isInCall: boolean;
  isConnecting: boolean;
  error: string | null;
}

export function useVideoCall() {
  const { user } = useAuth();
  const [state, setState] = useState<VideoCallState>({
    currentCall: null,
    isInCall: false,
    isConnecting: false,
    error: null,
  });

  const wsRef = useRef<WebSocket | null>(null);

  // Initialize WebSocket connection for call signaling
  useEffect(() => {
    if (!user) return;

    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws`;

    const ws = new WebSocket(wsUrl);
    wsRef.current = ws;

    ws.onopen = () => {
      console.log('Video call WebSocket connected');
      // Join user to call signaling
      ws.send(JSON.stringify({
        type: 'join-call-signaling',
        userId: (user as User).id
      }));
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        handleWebSocketMessage(data);
      } catch (error) {
        console.error('Error parsing WebSocket message:', error);
      }
    };

    ws.onclose = () => {
      console.log('Video call WebSocket disconnected');
    };

    ws.onerror = (error) => {
      console.error('Video call WebSocket error:', error);
      setState(prev => ({ ...prev, error: 'Connection failed' }));
    };

    return () => {
      ws.close();
    };
  }, [user]);

  const handleWebSocketMessage = (data: any) => {
    switch (data.type) {
      case 'call-started':
        setState(prev => ({
          ...prev,
          currentCall: data.call,
          isInCall: true,
          isConnecting: false,
        }));
        break;
      case 'call-ended':
        setState(prev => ({
          ...prev,
          currentCall: null,
          isInCall: false,
          isConnecting: false,
        }));
        break;
      case 'participant-joined':
        setState(prev => ({
          ...prev,
          currentCall: prev.currentCall ? {
            ...prev.currentCall,
            participants: data.participants,
          } : null,
        }));
        break;
      case 'participant-left':
        setState(prev => ({
          ...prev,
          currentCall: prev.currentCall ? {
            ...prev.currentCall,
            participants: data.participants,
          } : null,
        }));
        break;
      case 'call-invite':
        // Handle incoming call invitation
        console.log('Received call invite:', data);
        break;
    }
  };

  const startCall = async (participantIds: string[]) => {
    if (!user || state.isInCall) return;

    setState(prev => ({ ...prev, isConnecting: true, error: null }));

    try {
      const callData = await apiRequest('POST', '/api/video-calls', { participantIds });

      // WebSocket will handle the call state update
      setState(prev => ({ ...prev, isConnecting: false }));

      return callData;
    } catch (error) {
      console.error('Error starting call:', error);
      setState(prev => ({ 
        ...prev, 
        isConnecting: false, 
        error: 'Failed to start call' 
      }));
      throw error;
    }
  };

  const joinCall = async (callId: string) => {
    if (!user) return;

    try {
      await apiRequest('POST', `/api/video-calls/${callId}/join`);
    } catch (error) {
      console.error('Error joining call:', error);
      setState(prev => ({ ...prev, error: 'Failed to join call' }));
    }
  };

  const leaveCall = async () => {
    if (!state.currentCall) return;

    try {
      await apiRequest('POST', `/api/video-calls/${state.currentCall.id}/leave`);

      setState(prev => ({
        ...prev,
        currentCall: null,
        isInCall: false,
        isConnecting: false,
      }));
    } catch (error) {
      console.error('Error leaving call:', error);
      setState(prev => ({ ...prev, error: 'Failed to leave call' }));
    }
  };

  const endCall = async () => {
    if (!state.currentCall) return;

    try {
      await apiRequest('POST', `/api/video-calls/${state.currentCall.id}/end`);

      setState(prev => ({
        ...prev,
        currentCall: null,
        isInCall: false,
        isConnecting: false,
      }));
    } catch (error) {
      console.error('Error ending call:', error);
      setState(prev => ({ ...prev, error: 'Failed to end call' }));
    }
  };

  const toggleAudio = (enabled: boolean) => {
    if (wsRef.current && state.currentCall) {
      wsRef.current.send(JSON.stringify({
        type: 'audio-toggle',
        callId: state.currentCall.id,
        enabled,
      }));
    }
  };

  const toggleVideo = (enabled: boolean) => {
    if (wsRef.current && state.currentCall) {
      wsRef.current.send(JSON.stringify({
        type: 'video-toggle',
        callId: state.currentCall.id,
        enabled,
      }));
    }
  };

  return {
    ...state,
    startCall,
    joinCall,
    leaveCall,
    endCall,
    toggleAudio,
    toggleVideo,
  };
}

