// Edit Post Form Component
function EditPostForm({ post, onUpdate }: { post: any; onUpdate: () => void }) {
  const [editContent, setEditContent] = useState(post.content || '');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const { toast } = useToast();

  const handleUpdate = async () => {
    if (!editContent.trim()) {
      toast({
        title: "Error",
        description: "Post content cannot be empty.",
        variant: "destructive",
      });
      return;
    }

    setIsSubmitting(true);
    try {
      await apiRequest("PUT", `/api/posts/${post.id}`, {
        content: editContent
      });

      toast({
        title: "Post Updated",
        description: "Your post has been successfully updated.",
        className: "bg-white text-black border-gray-300",
      });

      onUpdate();
    } catch (error) {
      console.error('Error updating post:', error);
      toast({
        title: "Error",
        description: "Failed to update post. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDelete = async () => {
    setIsDeleting(true);
    try {
      await apiRequest("DELETE", `/api/posts/${post.id}`);

      toast({
        title: "Post Deleted",
        description: "Your post has been successfully deleted.",
        className: "bg-white text-black border-gray-300",
      });

      setShowDeleteDialog(false);
      onUpdate();
    } catch (error) {
      console.error('Error deleting post:', error);
      toast({
        title: "Error",
        description: "Failed to delete post. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsDeleting(false);
    }
  };

  return (
    <div className="space-y-4">
      <Textarea
        value={editContent}
        onChange={(e) => setEditContent(e.target.value)}
        placeholder="What's on your mind?"
        className="min-h-[120px] bg-background border-border text-foreground"
        data-testid="textarea-edit-post"
      />
      <div className="flex gap-2 justify-between">
        <Dialog open={showDeleteDialog} onOpenChange={setShowDeleteDialog}>
          <DialogTrigger asChild>
            <Button
              variant="destructive"
              disabled={isDeleting || isSubmitting}
              className="text-white"
              data-testid="button-delete-post"
            >
              Delete Post
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-md bg-card border-border">
            <DialogHeader>
              <DialogTitle className="text-foreground">Confirm Delete</DialogTitle>
            </DialogHeader>
            <div className="flex gap-2 justify-end pt-4">
              <Button
                variant="outline"
                onClick={() => setShowDeleteDialog(false)}
                disabled={isDeleting}
                className="border-border text-foreground"
              >
                Cancel
              </Button>
              <Button
                variant="destructive"
                onClick={handleDelete}
                disabled={isDeleting}
                className="text-white"
                data-testid="button-confirm-delete"
              >
                {isDeleting ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Deleting...
                  </>
                ) : (
                  "Delete"
                )}
              </Button>
            </div>
          </DialogContent>
        </Dialog>
        <div className="flex gap-2">
          <Button
            variant="outline"
            onClick={() => setEditContent(post.content || '')}
            disabled={isSubmitting || isDeleting}
            className="border-border text-foreground"
          >
            Reset
          </Button>
          <Button
            onClick={handleUpdate}
            disabled={isSubmitting || isDeleting || !editContent.trim()}
            className="bg-primary hover:bg-primary/90 text-primary-foreground"
            data-testid="button-update-post"
          >
            {isSubmitting ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Updating...
              </>
            ) : (
              "Update Post"
            )}
          </Button>
        </div>
      </div>
    </div>
  );
}

