import { useTranslation } from 'react-i18next';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Button } from '@/components/ui/button';
import { Globe } from 'lucide-react';
import { useState } from 'react';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';

const languages = [
  { code: 'en', name: 'English', flag: 'üá∫üá∏' },
  { code: 'es', name: 'Espa√±ol', flag: 'üá™üá∏' },
  { code: 'fr', name: 'Fran√ßais', flag: 'üá´üá∑' },
  { code: 'de', name: 'Deutsch', flag: 'üá©üá™' },
  { code: 'pt', name: 'Portugu√™s', flag: 'üáßüá∑' },
  { code: 'it', name: 'Italiano', flag: 'üáÆüáπ' },
  { code: 'zh', name: '‰∏≠Êñá', flag: 'üá®üá≥' },
  { code: 'ja', name: 'Êó•Êú¨Ë™û', flag: 'üáØüáµ' },
  { code: 'ko', name: 'ÌïúÍµ≠Ïñ¥', flag: 'üá∞üá∑' },
  { code: 'ar', name: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©', flag: 'üá∏üá¶' },
];

interface LanguageSelectorProps {
  variant?: 'dropdown' | 'select';
  showFlag?: boolean;
  className?: string;
}

export function LanguageSelector({ 
  variant = 'dropdown', 
  showFlag = true,
  className = '' 
}: LanguageSelectorProps) {
  const { i18n, t } = useTranslation();
  const [isDetected, setIsDetected] = useState(true);

  const currentLanguage = languages.find(lang => lang.code === i18n.language) || languages[0];

  const changeLanguage = (languageCode: string) => {
    i18n.changeLanguage(languageCode);
    setIsDetected(false);
    // Store language preference
    localStorage.setItem('i18nextLng', languageCode);
  };

  if (variant === 'select') {
    return (
      <div className={`space-y-2 ${className}`}>
        <label className="text-sm font-medium">
          {t('language.selectLanguage')}
        </label>
        <Select value={i18n.language} onValueChange={changeLanguage}>
          <SelectTrigger>
            <SelectValue>
              <div className="flex items-center gap-2">
                {showFlag && <span>{currentLanguage.flag}</span>}
                <span>{currentLanguage.name}</span>
                {isDetected && (
                  <span className="text-xs text-muted-foreground">
                    ({t('language.autoDetected')})
                  </span>
                )}
              </div>
            </SelectValue>
          </SelectTrigger>
          <SelectContent>
            {languages.map((language) => (
              <SelectItem key={language.code} value={language.code}>
                <div className="flex items-center gap-2">
                  {showFlag && <span>{language.flag}</span>}
                  <span>{language.name}</span>
                </div>
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>
    );
  }

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button 
          variant="ghost" 
          size="sm" 
          className={`gap-1 text-xs ${className} flex-shrink-0`}
          data-testid="button-language-selector"
        >
          <Globe className="w-3 h-3 flex-shrink-0" />
          {showFlag && <span className="flex-shrink-0">{currentLanguage.flag}</span>}
          <span className="hidden lg:inline truncate">{currentLanguage.name}</span>
          <span className="lg:hidden truncate">{currentLanguage.code.toUpperCase()}</span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-48">
        {languages.map((language) => (
          <DropdownMenuItem
            key={language.code}
            onClick={() => changeLanguage(language.code)}
            className={`flex items-center gap-3 ${
              i18n.language === language.code ? 'bg-accent' : ''
            }`}
            data-testid={`language-option-${language.code}`}
          >
            {showFlag && <span className="text-lg">{language.flag}</span>}
            <div className="flex flex-col">
              <span className="font-medium">{language.name}</span>
              {i18n.language === language.code && (
                <span className="text-xs text-muted-foreground">
                  {t('language.currentLanguage')}
                  {isDetected && ` ‚Ä¢ ${t('language.autoDetected')}`}
                </span>
              )}
            </div>
          </DropdownMenuItem>
        ))}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

client/src/components/PushNotificationSe.content:
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Switch } from '@/components/ui/switch';
import { Bell, BellOff, Smartphone, X } from 'lucide-react';
import { usePushNotifications } from '@/hooks/usePushNotifications';
import { cn } from '@/lib/utils';

interface PushNotificationSetupProps {
  onDismiss?: () => void;
  compact?: boolean;
  darkStyle?: boolean;
}

export function PushNotificationSetup({ onDismiss, compact = false, darkStyle = false }: PushNotificationSetupProps) {
  const { permission, requestPermission, isSupported } = usePushNotifications();
  const [isRequesting, setIsRequesting] = useState(false);

  const handleToggle = async (checked: boolean) => {
    if (!checked) return; // Can't disable once granted, would need browser settings

    setIsRequesting(true);
    try {
      await requestPermission();
    } finally {
      setIsRequesting(false);
    }
  };

  // Don't show if not supported
  if (!isSupported) {
    return null;
  }

  const isEnabled = permission === 'granted';
  const isBlocked = permission === 'denied';

  return (
    <Card className={cn(
      "transition-all duration-200",
      darkStyle 
        ? "bg-black border-gray-700 text-white" 
        : "bg-card border-border"
    )}>
      <CardContent className="p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className={cn(
              "p-2 rounded-full transition-colors",
              darkStyle 
                ? isEnabled ? "bg-green-600" : "bg-gray-600"
                : isEnabled ? "bg-green-500" : "bg-muted"
            )}>
              {isEnabled ? (
                <Bell className={cn(
                  "w-4 h-4",
                  darkStyle ? "text-white" : "text-white"
                )} />
              ) : (
                <BellOff className={cn(
                  "w-4 h-4",
                  darkStyle ? "text-gray-300" : "text-muted-foreground"
                )} />
              )}
            </div>
            <div>
              <h3 className={cn(
                "font-medium text-sm",
                darkStyle ? "text-white" : "text-foreground"
              )}>
                Push Notifications
              </h3>
              <p className={cn(
                "text-xs",
                darkStyle ? "text-gray-300" : "text-muted-foreground"
              )}>
                {isEnabled 
                  ? "Receiving real-time alerts" 
                  : isBlocked 
                    ? "Blocked - check browser settings"
                    : "Get instant kliq activity alerts"
                }
              </p>
            </div>
          </div>

          <div className="flex items-center gap-2">
            <Switch
              checked={isEnabled}
              onCheckedChange={handleToggle}
              disabled={isRequesting || isBlocked}
              className={cn(
                "data-[state=checked]:bg-green-500",
                isBlocked && "opacity-50 cursor-not-allowed"
              )}
            />
            {onDismiss && !isEnabled && (
              <Button
                variant="ghost"
                size="sm"
                onClick={onDismiss}
                className="h-8 w-8 p-0 ml-1"
              >
                <X className="w-3 h-3" />
              </Button>
            )}
          </div>
        </div>

        {isBlocked && (
          <div className={cn(
            "mt-3 p-2 rounded text-xs",
            darkStyle ? "bg-gray-800 text-gray-300" : "bg-orange-50 text-orange-700 border border-orange-200"
          )}>
            To enable notifications, click the notification icon in your browser's address bar and allow notifications.
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/hooks/usePushNotifications.ts.content:
import { useEffect, useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import type { Notification } from '@shared/schema';

interface PushNotificationHook {
  permission: NotificationPermission;
  requestPermission: () => Promise<NotificationPermission>;
  showNotification: (title: string, options?: NotificationOptions) => void;
  isSupported: boolean;
}

export function usePushNotifications(): PushNotificationHook {
  const [permission, setPermission] = useState<NotificationPermission>('default');
  const [lastNotificationId, setLastNotificationId] = useState<string | null>(null);

  // Check if browser supports notifications
  const isSupported = 'Notification' in window;

  // Initialize permission state
  useEffect(() => {
    if (isSupported) {
      setPermission(Notification.permission);
    }
  }, [isSupported]);

  // Watch for new notifications and show banner alerts
  const { data: notifications = [] } = useQuery<Notification[]>({
    queryKey: ["/api/notifications"],
    refetchInterval: 30000, // Check every 30 seconds for new notifications
    staleTime: 15000, // Consider data fresh for 15 seconds
    enabled: isSupported && permission === 'granted',
  });

  // Show banner notification for new unread notifications
  useEffect(() => {
    if (!isSupported || permission !== 'granted' || notifications.length === 0) {
      return;
    }

    // Find the most recent unread notification
    const unreadNotifications = notifications
      .filter((n: Notification) => !n.isRead && n.isVisible)
      .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());

    if (unreadNotifications.length === 0) {
      return;
    }

    const latestNotification = unreadNotifications[0];

    // Only show notification if it's a new one (different from last shown)
    if (latestNotification.id !== lastNotificationId) {
      setLastNotificationId(latestNotification.id);

      // Create notification title and body based on type
      let title = 'MyKliq';
      let body = latestNotification.message;
      let icon = '/favicon.ico';

      switch (latestNotification.type) {
        case 'post_like':
          title = '‚ù§Ô∏è New Like';
          break;
        case 'comment':
          title = 'üí¨ New Comment';
          break;
        case 'friend_request':
          title = 'üë• Friend Request';
          break;
        case 'event_invite':
          title = 'üìÖ Event Invitation';
          break;
        case 'message':
          title = 'üì® New Message';
          break;
        case 'birthday':
          title = 'üéÇ Birthday Reminder';
          break;
        case 'meetup_invite':
          title = 'üìç Meetup Invitation';
          break;
        case 'live_stream':
          title = 'üî¥ Live Stream';
          break;
        default:
          title = 'üîî MyKliq Notification';
      }

      showNotification(title, {
        body,
        icon,
        badge: icon,
        tag: latestNotification.type, // Replace previous notifications of same type
        requireInteraction: false,
        silent: false,
        data: {
          notificationId: latestNotification.id,
          type: latestNotification.type,
          url: window.location.origin
        }
      });
    }
  }, [notifications, permission, isSupported, lastNotificationId]);

  const requestPermission = async (): Promise<NotificationPermission> => {
    if (!isSupported) {
      return 'denied';
    }

    try {
      const result = await Notification.requestPermission();
      setPermission(result);
      return result;
    } catch (error) {
      console.error('Error requesting notification permission:', error);
      return 'denied';
    }
  };

  const showNotification = (title: string, options: NotificationOptions = {}) => {
    if (!isSupported || permission !== 'granted') {
      return;
    }

    try {
      const notification = new Notification(title, {
        ...options,
        // Auto-close after 6 seconds
        ...(!options.requireInteraction && { 
          // Add timestamp to ensure uniqueness
          timestamp: Date.now()
        })
      });

      // Auto-close notification after 6 seconds if not set to require interaction
      if (!options.requireInteraction) {
        setTimeout(() => {
          notification.close();
        }, 6000);
      }

      // Handle notification click
      notification.onclick = () => {
        window.focus(); // Focus the browser window
        notification.close();

        // If there's URL data, navigate to it
        if (options.data?.url) {
          window.location.href = options.data.url;
        }
      };

      // Handle notification close
      notification.onclose = () => {
        // Optional: Track notification interactions
        console.log('Notification closed');
      };

    } catch (error) {
      console.error('Error showing notification:', error);
    }
  };

  return {
    permission,
    requestPermission,
    showNotification,
    isSupported
  };
}

