import Redis from 'redis';

let redis: Redis.RedisClientType | null = null;

// Initialize Redis connection with production optimizations
export async function initializeRedis() {
  try {
    // Skip Redis initialization in development if not available
    if (!process.env.REDIS_URL && process.env.NODE_ENV === 'development') {
      console.log('Redis not available in development, using memory cache fallback');
      return null;
    }

    redis = Redis.createClient({
      url: process.env.REDIS_URL || 'redis://localhost:6379',
      socket: {
        connectTimeout: 3000,
        keepAlive: true,
        reconnectStrategy: (retries) => {
          if (retries > 5) return false; // More resilient reconnection
          return Math.min(retries * 50, 500); // Faster reconnect attempts
        }
      },
      // High-performance Redis settings
      commandsQueueMaxLength: 1000,
      enableOfflineQueue: true,
      maxRetriesPerRequest: 3,
      lazyConnect: false,
      retryDelayOnFailover: 100
    });

    redis.on('error', (err) => {
      console.error('Redis Client Error:', err);
    });

    redis.on('connect', () => {
      console.log('Redis connected successfully');
    });

    redis.on('ready', () => {
      console.log('Redis ready for operations');
    });

    // Connect to Redis
    await redis.connect();

    return redis;
  } catch (error) {
    console.error('Failed to initialize Redis:', error);
    // Gracefully degrade - continue without cache
    redis = null;
    return null;
  }
}

// Get Redis client
export function getRedisClient() {
  return redis;
}

// Cache operations with fallback
export async function setCache(key: string, value: any, ttlSeconds = 300): Promise<void> {
  if (!redis) return; // Graceful degradation

  try {
    await redis.setEx(key, ttlSeconds, JSON.stringify(value));
  } catch (error) {
    console.error('Redis SET error:', error);
  }
}

export async function getCache<T>(key: string): Promise<T | null> {
  if (!redis) return null; // Graceful degradation

  try {
    const cached = await redis.get(key);
    return cached ? JSON.parse(cached) : null;
  } catch (error) {
    console.error('Redis GET error:', error);
    return null;
  }
}

export async function deleteCache(key: string): Promise<void> {
  if (!redis) return;

  try {
    await redis.del(key);
  } catch (error) {
    console.error('Redis DELETE error:', error);
  }
}

export async function deleteCachePattern(pattern: string): Promise<void> {
  if (!redis) return;

  try {
    const keys = await redis.keys(pattern);
    if (keys.length > 0) {
      await redis.del(keys);
    }
  } catch (error) {
    console.error('Redis DELETE PATTERN error:', error);
  }
}

// Enhanced cache with automatic invalidation
export async function getCachedOrFetch<T>(
  key: string, 
  fetchFunction: () => Promise<T>, 
  ttlSeconds = 300
): Promise<T> {
  // Try cache first
  const cached = await getCache<T>(key);
  if (cached !== null) {
    return cached;
  }

  // Fetch fresh data
  const data = await fetchFunction();

  // Cache the result
  await setCache(key, data, ttlSeconds);

  return data;
}

// Initialize Redis on startup
initializeRedis().catch(console.error);

