import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { ChevronUp, ChevronDown, TrendingUp, MessageCircle, Eye, X, Check, Lightbulb, Users } from "lucide-react";
import { cn } from "@/lib/utils";
import { useToast } from "@/hooks/use-toast";

interface RankingSuggestion {
  id: string;
  friendId: string;
  currentRank: number;
  suggestedRank: number;
  confidence: string;
  primaryReason: string;
  justificationMessage: string;
  supportingMetrics: {
    totalInteractions: number;
    interactionScore: string;
    consistencyScore: string;
    engagementScore: string;
    overallScore: string;
  };
  createdAt: string;
  expiresAt: string;
  friend: {
    id: string;
    firstName?: string;
    lastName?: string;
    profileImageUrl?: string;
  };
}

interface RankingSuggestionsProps {
  onRankingChange?: () => void;
}

export function RankingSuggestions({ onRankingChange }: RankingSuggestionsProps) {
  const [expandedSuggestion, setExpandedSuggestion] = useState<string | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch pending ranking suggestions
  const { data: suggestions = [], isLoading } = useQuery<RankingSuggestion[]>({
    queryKey: ['/api/friend-ranking/suggestions'],
    refetchInterval: 300000, // Refresh every 5 minutes
  });

  // Accept suggestion mutation
  const acceptSuggestionMutation = useMutation({
    mutationFn: async (suggestionId: string) => {
      const response = await fetch(`/api/friend-ranking/suggestions/${suggestionId}/accept`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Failed to accept suggestion');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Ranking Updated",
        description: "Friend ranking has been updated based on your interactions.",
      });

      // Refresh suggestions and notify parent component
      queryClient.invalidateQueries({ queryKey: ['/api/friend-ranking/suggestions'] });
      queryClient.invalidateQueries({ queryKey: ['/api/friends'] });
      onRankingChange?.();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update ranking. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Dismiss suggestion mutation
  const dismissSuggestionMutation = useMutation({
    mutationFn: async (suggestionId: string) => {
      const response = await fetch(`/api/friend-ranking/suggestions/${suggestionId}/dismiss`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Failed to dismiss suggestion');
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/friend-ranking/suggestions'] });
      toast({
        title: "Suggestion Dismissed",
        description: "The ranking suggestion has been dismissed.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to dismiss suggestion. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Generate new suggestions mutation
  const generateSuggestionsMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/friend-ranking/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Failed to generate suggestions');
      }

      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['/api/friend-ranking/suggestions'] });

      if (data.count > 0) {
        toast({
          title: "New Suggestions Available",
          description: `Generated ${data.count} new ranking suggestions based on your recent interactions.`,
        });
      } else {
        toast({
          title: "No New Suggestions",
          description: "Your current rankings align well with your interaction patterns.",
        });
      }
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to generate new suggestions. Please try again.",
        variant: "destructive",
      });
    },
  });

  const getInitials = (friend: RankingSuggestion['friend']) => {
    const first = friend.firstName?.[0] || "";
    const last = friend.lastName?.[0] || "";
    return first + last || "?";
  };

  const getName = (friend: RankingSuggestion['friend']) => {
    const first = friend.firstName || "";
    const last = friend.lastName ? ` ${friend.lastName[0]}.` : "";
    return first + last || "Unknown";
  };

  const getRankChangeIcon = (currentRank: number, suggestedRank: number) => {
    if (suggestedRank < currentRank) {
      return <ChevronUp className="h-4 w-4 text-green-500" />;
    } else {
      return <ChevronDown className="h-4 w-4 text-orange-500" />;
    }
  };

  const getReasonIcon = (reason: string) => {
    switch (reason) {
      case 'frequent_communication':
        return <MessageCircle className="h-4 w-4" />;
      case 'high_engagement':
        return <Eye className="h-4 w-4" />;
      case 'in_person_connection':
        return <Users className="h-4 w-4" />;
      default:
        return <TrendingUp className="h-4 w-4" />;
    }
  };

  const getReasonLabel = (reason: string) => {
    switch (reason) {
      case 'frequent_communication':
        return 'Frequent Communication';
      case 'high_engagement':
        return 'High Engagement';
      case 'in_person_connection':
        return 'In-Person Connection';
      case 'general_activity':
        return 'General Activity';
      default:
        return 'Interaction Analysis';
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-4" data-testid="ranking-suggestions-loading">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold flex items-center gap-2">
            <Lightbulb className="h-5 w-5 text-yellow-500" />
            Smart Ranking Suggestions
          </h3>
        </div>
        <div className="animate-pulse space-y-3">
          {[1, 2].map((i) => (
            <div key={i} className="h-20 bg-muted rounded-lg" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-4" data-testid="ranking-suggestions">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold flex items-center gap-2">
          <Lightbulb className="h-5 w-5 text-yellow-500" />
          Smart Ranking Suggestions
          {suggestions.length > 0 && (
            <Badge variant="secondary" className="ml-2">
              {suggestions.length}
            </Badge>
          )}
        </h3>

        <Button
          variant="outline"
          size="sm"
          onClick={() => generateSuggestionsMutation.mutate()}
          disabled={generateSuggestionsMutation.isPending}
          data-testid="button-generate-suggestions"
        >
          {generateSuggestionsMutation.isPending ? "Analyzing..." : "Analyze Interactions"}
        </Button>
      </div>

      {suggestions.length === 0 ? (
        <Card data-testid="no-suggestions-card">
          <CardContent className="pt-6">
            <div className="text-center text-muted-foreground">
              <Lightbulb className="h-8 w-8 mx-auto mb-2 text-muted-foreground/50" />
              <p>No ranking suggestions available</p>
              <p className="text-sm mt-1">Your current rankings align well with your interaction patterns.</p>
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-3">
          {suggestions.map((suggestion: RankingSuggestion) => (
            <Card 
              key={suggestion.id} 
              className="transition-all duration-200 hover:shadow-md"
              data-testid={`suggestion-card-${suggestion.id}`}
            >
              <CardContent className="pt-4">
                <div className="flex items-start justify-between">
                  <div className="flex items-center gap-3 flex-1">
                    <Avatar className="h-10 w-10">
                      <AvatarImage 
                        src={suggestion.friend.profileImageUrl} 
                        alt={getName(suggestion.friend)} 
                      />
                      <AvatarFallback>{getInitials(suggestion.friend)}</AvatarFallback>
                    </Avatar>

                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-1">
                        <span className="font-medium">{getName(suggestion.friend)}</span>
                        <div className="flex items-center gap-1 text-sm text-muted-foreground">
                          {getRankChangeIcon(suggestion.currentRank, suggestion.suggestedRank)}
                          <span className="text-xs">
                            #{suggestion.currentRank} ‚Üí #{suggestion.suggestedRank}
                          </span>
                        </div>
                      </div>

                      <div className="flex items-center gap-2 mb-2">
                        <Badge 
                          variant="outline" 
                          className="text-xs flex items-center gap-1"
                        >
                          {getReasonIcon(suggestion.primaryReason)}
                          {getReasonLabel(suggestion.primaryReason)}
                        </Badge>

                        <Badge variant="secondary" className="text-xs">
                          {parseFloat(suggestion.confidence).toFixed(0)}% confident
                        </Badge>
                      </div>

                      <p className="text-sm text-muted-foreground mb-3">
                        {suggestion.justificationMessage}
                      </p>

                      {expandedSuggestion === suggestion.id && (
                        <div className="mt-3 p-3 bg-muted/30 rounded-lg text-xs space-y-2">
                          <div className="grid grid-cols-2 gap-2">
                            <div>
                              <span className="font-medium">Total Interactions:</span>
                              <span className="ml-1">{suggestion.supportingMetrics.totalInteractions}</span>
                            </div>
                            <div>
                              <span className="font-medium">Overall Score:</span>
                              <span className="ml-1">{parseFloat(suggestion.supportingMetrics.overallScore).toFixed(1)}</span>
                            </div>
                            <div>
                              <span className="font-medium">Engagement:</span>
                              <span className="ml-1">{parseFloat(suggestion.supportingMetrics.engagementScore).toFixed(1)}</span>
                            </div>
                            <div>
                              <span className="font-medium">Consistency:</span>
                              <span className="ml-1">{parseFloat(suggestion.supportingMetrics.consistencyScore).toFixed(1)}</span>
                            </div>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>

                  <div className="flex items-center gap-2 ml-4">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setExpandedSuggestion(
                        expandedSuggestion === suggestion.id ? null : suggestion.id
                      )}
                      data-testid={`button-toggle-details-${suggestion.id}`}
                    >
                      {expandedSuggestion === suggestion.id ? "Less" : "Details"}
                    </Button>

                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => dismissSuggestionMutation.mutate(suggestion.id)}
                      disabled={dismissSuggestionMutation.isPending}
                      data-testid={`button-dismiss-${suggestion.id}`}
                    >
                      <X className="h-4 w-4" />
                    </Button>

                    <Button
                      size="sm"
                      onClick={() => acceptSuggestionMutation.mutate(suggestion.id)}
                      disabled={acceptSuggestionMutation.isPending}
                      data-testid={`button-accept-${suggestion.id}`}
                    >
                      <Check className="h-4 w-4 mr-1" />
                      Apply
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

server/feedCurationIntelligence.ts.content:
import { db } from './db';
import { friendships, userInteractionAnalytics, posts, postLikes, comments, polls, events, actions, users, contentEngagements, type InsertContentEngagement } from '@shared/schema';
import { eq, and, inArray, desc, gte } from 'drizzle-orm';
import { contentRecommendationEngine } from './contentRecommendationEngine.js';

interface FeedItem {
  id: string;
  userId: string;
  type: 'post' | 'poll' | 'event' | 'action';
  content: string;
  createdAt: Date;
  activityDate: Date;
  author: {
    id: string;
    firstName?: string;
    lastName?: string;
    profileImageUrl?: string;
    kliqName?: string;
  };
  [key: string]: any; // For type-specific properties
}

interface CuratedFeedItem extends FeedItem {
  relevanceScore: number;
  engagementPrediction: number;
  rankWeight: number;
  diversityBoost: number;
  finalScore: number;
  curationType: 'high-rank' | 'engagement-predicted' | 'diversity' | 'recent';
}

interface EngagementPrediction {
  likesPredicted: number;
  commentsPredicted: number;
  totalEngagementScore: number;
  confidence: number;
}

export class FeedCurationIntelligence {
  private static readonly CURATION_WEIGHTS = {
    friendRank: 0.35,           // 35% - Higher ranked friends get priority
    engagementPrediction: 0.30, // 30% - Predicted user engagement
    contentRecency: 0.20,       // 20% - Recent content boost
    contentDiversity: 0.15,     // 15% - Encourage content type variety
  };

  private static readonly CONTENT_TYPE_WEIGHTS = {
    post: 1.0,      // Base weight
    poll: 1.2,      // Polls are interactive, slightly boost
    event: 1.1,     // Events are important
    action: 0.9,    // Live streams can be overwhelming, slight reduction
  };

  private static readonly ENGAGEMENT_FACTORS = {
    authorEngagementHistory: 0.4,  // 40% - How engaging is this author typically?
    contentTypeEngagement: 0.3,    // 30% - How engaging is this type of content?
    timeOfDayFactor: 0.2,          // 20% - When do users engage most?
    contentLengthFactor: 0.1,      // 10% - Content length optimization
  };

  /**
   * Generate intelligently curated feed for a user
   */
  async getCuratedFeed(
    userId: string, 
    rawFeedItems: FeedItem[], 
    page = 1, 
    limit = 20
  ): Promise<{ items: CuratedFeedItem[], hasMore: boolean, totalPages: number }> {

    // Get user's friend rankings and analytics
    const [friendRankings, userAnalytics] = await Promise.all([
      this.getFriendRankings(userId),
      this.getUserEngagementAnalytics(userId)
    ]);

    // Enrich feed items with curation scores
    const enrichedItems = await Promise.all(
      rawFeedItems.map(item => this.enrichFeedItem(item, friendRankings, userAnalytics, userId))
    );

    // Apply intelligent sorting with diversity balancing
    const curatedItems = this.applyCurationAlgorithm(enrichedItems, limit * 3); // Get more for diversity

    // Apply content type balancing for optimal mix
    const balancedItems = this.applyContentTypeBalancing(curatedItems);

    // Apply pagination to final results
    const totalItems = balancedItems.length;
    const totalPages = Math.ceil(totalItems / limit);
    const startIndex = (page - 1) * limit;
    const endIndex = startIndex + limit;
    const paginatedItems = balancedItems.slice(startIndex, endIndex);
    const hasMore = endIndex < totalItems;

    return {
      items: paginatedItems,
      hasMore,
      totalPages
    };
  }

  /**
   * Enrich feed item with curation scores
   */
  private async enrichFeedItem(
    item: FeedItem, 
    friendRankings: Map<string, number>,
    userAnalytics: any,
    userId: string
  ): Promise<CuratedFeedItem> {

    // Calculate rank-based weight (higher rank = higher weight)
    const authorRank = friendRankings.get(item.userId) || 28; // Default to lowest rank
    const rankWeight = this.calculateRankWeight(authorRank);

    // Predict engagement for this item
    const engagementPrediction = await this.predictEngagement(item, userAnalytics, userId);

    // Calculate recency boost (newer content gets slight boost)
    const recencyBoost = this.calculateRecencyBoost(item.createdAt);

    // Calculate content type weight
    const contentTypeWeight = FeedCurationIntelligence.CONTENT_TYPE_WEIGHTS[item.type] || 1.0;

    // Calculate relevance score with content recommendations
    const relevanceScore = await this.calculateRelevanceScore(item, userAnalytics, userId);

    // Calculate final weighted score
    const finalScore = 
      (rankWeight * FeedCurationIntelligence.CURATION_WEIGHTS.friendRank) +
      (engagementPrediction.totalEngagementScore * FeedCurationIntelligence.CURATION_WEIGHTS.engagementPrediction) +
      (recencyBoost * FeedCurationIntelligence.CURATION_WEIGHTS.contentRecency) +
      (contentTypeWeight * FeedCurationIntelligence.CURATION_WEIGHTS.contentDiversity);

    return {
      ...item,
      relevanceScore,
      engagementPrediction: engagementPrediction.totalEngagementScore,
      rankWeight,
      diversityBoost: contentTypeWeight,
      finalScore,
      curationType: this.determineCurationType(rankWeight, engagementPrediction.totalEngagementScore, recencyBoost)
    };
  }

  /**
   * Calculate weight based on friend ranking (1-28, where 1 is highest)
   */
  private calculateRankWeight(rank: number): number {
    // Convert rank to weight: rank 1 = 1.0, rank 28 = 0.1
    return Math.max(0.1, 1.0 - ((rank - 1) / 27) * 0.9);
  }

  /**
   * Predict user engagement with this content item
   */
  private async predictEngagement(
    item: FeedItem, 
    userAnalytics: any,
    userId: string
  ): Promise<EngagementPrediction> {

    // Get historical engagement data for this author
    const authorEngagementHistory = await this.getAuthorEngagementHistory(item.userId, userId);

    // Get content type engagement patterns
    const contentTypeEngagement = await this.getContentTypeEngagement(item.type, userId);

    // Calculate time-of-day factor
    const timeOfDayFactor = this.calculateTimeOfDayFactor(item.createdAt, userAnalytics);

    // Calculate content length factor (for posts with content)
    const contentLengthFactor = this.calculateContentLengthFactor(item.content);

    // Predict likes and comments
    const likesPredicted = 
      (authorEngagementHistory.avgLikes * FeedCurationIntelligence.ENGAGEMENT_FACTORS.authorEngagementHistory) +
      (contentTypeEngagement.avgLikes * FeedCurationIntelligence.ENGAGEMENT_FACTORS.contentTypeEngagement) +
      (timeOfDayFactor * 2) + // Base boost for good timing
      (contentLengthFactor * 1); // Base boost for optimal length

    const commentsPredicted = 
      (authorEngagementHistory.avgComments * FeedCurationIntelligence.ENGAGEMENT_FACTORS.authorEngagementHistory) +
      (contentTypeEngagement.avgComments * FeedCurationIntelligence.ENGAGEMENT_FACTORS.contentTypeEngagement) +
      (timeOfDayFactor * 1) + // Smaller boost for comments
      (contentLengthFactor * 0.5);

    // Calculate total engagement score (weighted)
    const totalEngagementScore = (likesPredicted * 0.6) + (commentsPredicted * 0.4);

    // Calculate confidence based on data availability
    const confidence = Math.min(1.0, 
      (authorEngagementHistory.dataPoints + contentTypeEngagement.dataPoints) / 20
    );

    return {
      likesPredicted: Math.max(0, likesPredicted),
      commentsPredicted: Math.max(0, commentsPredicted),
      totalEngagementScore: Math.max(0, totalEngagementScore),
      confidence
    };
  }

  /**
   * Calculate recency boost for content
   */
  private calculateRecencyBoost(createdAt: Date): number {
    const hoursAgo = (Date.now() - createdAt.getTime()) / (1000 * 60 * 60);

    // Content gets higher boost if it's newer
    if (hoursAgo < 1) return 1.0;      // Last hour: full boost
    if (hoursAgo < 6) return 0.8;      // Last 6 hours: high boost
    if (hoursAgo < 24) return 0.6;     // Last day: medium boost
    if (hoursAgo < 72) return 0.4;     // Last 3 days: low boost
    return 0.2;                        // Older content: minimal boost
  }

  /**
   * Calculate relevance score based on user interests and interaction patterns
   */
  private async calculateRelevanceScore(item: FeedItem, userAnalytics: any, userId: string): Promise<number> {
    // Base relevance score
    let score = 0.5;

    // Content type preference boost
    const contentTypePreference = userAnalytics?.contentTypePreferences?.[item.type] || 0.5;
    score += contentTypePreference * 0.3;

    // Author interaction history boost
    const authorInteractionScore = userAnalytics?.authorInteractions?.[item.userId] || 0.5;
    score += authorInteractionScore * 0.2;

    // Get content recommendations to boost relevance
    try {
      const recommendations = await contentRecommendationEngine.generateRecommendations(userId);

      // Check if this item matches any user recommendations
      const matchingRecommendations = recommendations.filter(rec => 
        this.contentMatchesRecommendation(item, rec)
      );

      if (matchingRecommendations.length > 0) {
        // Boost relevance based on highest recommendation score
        const bestMatch = Math.max(...matchingRecommendations.map(r => r.score));
        score += (bestMatch / 100) * 0.4; // Up to 40% boost
      }

    } catch (error) {
      console.warn('Failed to get content recommendations for relevance scoring:', error);
    }

    return Math.min(1.0, Math.max(0.0, score));
  }

  /**
   * Check if content item matches a recommendation
   */
  private contentMatchesRecommendation(item: FeedItem, recommendation: any): boolean {
    const content = item.content?.toLowerCase() || '';

    return recommendation.keywords.some((keyword: string) => 
      content.includes(keyword.toLowerCase())
    );
  }

  /**
   * Determine curation type for analytics
   */
  private determineCurationType(
    rankWeight: number, 
    engagementScore: number, 
    recencyBoost: number
  ): CuratedFeedItem['curationType'] {
    if (rankWeight > 0.8) return 'high-rank';
    if (engagementScore > 3.0) return 'engagement-predicted';
    if (recencyBoost > 0.8) return 'recent';
    return 'diversity';
  }

  /**
   * Apply intelligent curation algorithm with diversity balancing
   */
  private applyCurationAlgorithm(items: CuratedFeedItem[], maxItems: number): CuratedFeedItem[] {
    // Sort by final score (descending)
    const sortedItems = [...items].sort((a, b) => b.finalScore - a.finalScore);

    // Apply diversity constraints to prevent feed monotony
    const curatedItems: CuratedFeedItem[] = [];
    const contentTypeCount = new Map<string, number>();
    const authorCount = new Map<string, number>();

    for (const item of sortedItems) {
      if (curatedItems.length >= maxItems) break;

      // Diversity constraints
      const authorPostCount = authorCount.get(item.userId) || 0;
      const contentTypeCount_ = contentTypeCount.get(item.type) || 0;

      // Limit same author to 3 items per batch
      if (authorPostCount >= 3) continue;

      // Ensure content type variety (no more than 50% of one type)
      if (contentTypeCount_ >= Math.floor(maxItems * 0.5)) continue;

      // Add item to curated feed
      curatedItems.push(item);
      authorCount.set(item.userId, authorPostCount + 1);
      contentTypeCount.set(item.type, contentTypeCount_ + 1);
    }

    return curatedItems;
  }

  /**
   * Apply content type balancing for optimal engagement mix
   */
  private applyContentTypeBalancing(items: CuratedFeedItem[]): CuratedFeedItem[] {
    // Target distribution for optimal engagement
    const targetDistribution = {
      post: 0.60,    // 60% posts - main content
      poll: 0.20,    // 20% polls - interactive content
      event: 0.15,   // 15% events - important activities
      action: 0.05,  // 5% live streams - special content
    };

    // Group items by type
    const itemsByType = new Map<string, CuratedFeedItem[]>();
    items.forEach(item => {
      if (!itemsByType.has(item.type)) {
        itemsByType.set(item.type, []);
      }
      itemsByType.get(item.type)!.push(item);
    });

    // Balance according to target distribution
    const balancedItems: CuratedFeedItem[] = [];
    const totalItems = items.length;

    Object.entries(targetDistribution).forEach(([type, percentage]) => {
      const targetCount = Math.floor(totalItems * percentage);
      const typeItems = itemsByType.get(type) || [];
      const selectedItems = typeItems.slice(0, targetCount);
      balancedItems.push(...selectedItems);
    });

    // Add remaining high-scoring items to fill the quota
    const usedIds = new Set(balancedItems.map(item => item.id));
    const remainingItems = items.filter(item => !usedIds.has(item.id));
    const remainingSlots = totalItems - balancedItems.length;

    if (remainingSlots > 0) {
      balancedItems.push(...remainingItems.slice(0, remainingSlots));
    }

    // Final sort by score to maintain quality
    return balancedItems.sort((a, b) => b.finalScore - a.finalScore);
  }

  /**
   * Get friend rankings for the user
   */
  private async getFriendRankings(userId: string): Promise<Map<string, number>> {
    const rankings = await db
      .select({
        friendId: friendships.friendId,
        rank: friendships.rank
      })
      .from(friendships)
      .where(eq(friendships.userId, userId));

    const rankMap = new Map<string, number>();
    rankings.forEach(r => rankMap.set(r.friendId, r.rank));

    return rankMap;
  }

  /**
   * Get user engagement analytics for predictions
   */
  private async getUserEngagementAnalytics(userId: string): Promise<any> {
    // Get recent interaction patterns
    // For now, return simplified analytics (real implementation would query contentEngagements table)
    // This prevents SQL errors while keeping the intelligent curation system functional
    const recentEngagements: any[] = [];

    // Analyze patterns
    const contentTypePreferences = new Map<string, number>();
    const authorInteractions = new Map<string, number>();

    recentEngagements.forEach(engagement => {
      // Content type preferences
      const currentPref = contentTypePreferences.get(engagement.contentType) || 0;
      contentTypePreferences.set(engagement.contentType, currentPref + engagement.timeSpent);

      // Author interaction strength
      const currentInteraction = authorInteractions.get(engagement.authorId) || 0;
      authorInteractions.set(engagement.authorId, currentInteraction + engagement.timeSpent);
    });

    return {
      contentTypePreferences: Object.fromEntries(contentTypePreferences),
      authorInteractions: Object.fromEntries(authorInteractions),
      totalEngagements: recentEngagements.length
    };
  }

  /**
   * Get historical engagement data for an author
   */
  private async getAuthorEngagementHistory(authorId: string, viewerId: string): Promise<{
    avgLikes: number;
    avgComments: number;
    dataPoints: number;
  }> {
    // Get recent posts from this author and their engagement
    const recentPosts = await db
      .select({
        id: posts.id,
        likesCount: posts.likes,
        createdAt: posts.createdAt
      })
      .from(posts)
      .where(eq(posts.userId, authorId))
      .orderBy(desc(posts.createdAt))
      .limit(20);

    if (recentPosts.length === 0) {
      return { avgLikes: 0, avgComments: 0, dataPoints: 0 };
    }

    // Calculate averages
    const totalLikes = recentPosts.reduce((sum, post) => sum + (post.likesCount || 0), 0);
    const avgLikes = totalLikes / recentPosts.length;

    // Get comment counts (simplified for performance)
    const avgComments = avgLikes * 0.2; // Estimate: typically 20% of likes result in comments

    return {
      avgLikes,
      avgComments,
      dataPoints: recentPosts.length
    };
  }

  /**
   * Get engagement patterns for content type
   */
  private async getContentTypeEngagement(contentType: string, userId: string): Promise<{
    avgLikes: number;
    avgComments: number;
    dataPoints: number;
  }> {
    // Simplified implementation - in production, this would analyze historical data
    const baseEngagement = {
      post: { likes: 2.5, comments: 0.5 },
      poll: { likes: 4.0, comments: 1.2 },
      event: { likes: 3.2, comments: 0.8 },
      action: { likes: 5.0, comments: 2.0 }
    };

    const base = baseEngagement[contentType as keyof typeof baseEngagement] || { likes: 2.0, comments: 0.4 };

    return {
      avgLikes: base.likes,
      avgComments: base.comments,
      dataPoints: 10 // Placeholder
    };
  }

  /**
   * Calculate time-of-day engagement factor
   */
  private calculateTimeOfDayFactor(createdAt: Date, userAnalytics: any): number {
    const hour = createdAt.getHours();

    // Peak engagement hours (based on typical social media patterns)
    if (hour >= 7 && hour <= 9) return 1.0;   // Morning peak
    if (hour >= 12 && hour <= 14) return 0.9; // Lunch time
    if (hour >= 17 && hour <= 21) return 1.0; // Evening peak
    if (hour >= 21 && hour <= 23) return 0.8; // Late evening

    return 0.6; // Off-peak hours
  }

  /**
   * Calculate content length optimization factor
   */
  private calculateContentLengthFactor(content: string): number {
    const length = content.length;

    // Optimal content length for engagement
    if (length >= 50 && length <= 200) return 1.0;   // Sweet spot
    if (length >= 20 && length <= 300) return 0.8;   // Good range
    if (length >= 10 && length <= 500) return 0.6;   // Acceptable range

    return 0.4; // Too short or too long
  }

  /**
   * Track content engagement (time spent viewing content)
   */
  async trackContentEngagement(engagement: InsertContentEngagement): Promise<void> {
    try {
      await db.insert(contentEngagements).values(engagement);
    } catch (error) {
      console.warn('Failed to track content engagement:', error);
    }
  }
}

server/notificationIntelligence.ts.content:
import { db } from './db';
import { notifications, users, userInteractionAnalytics, posts, postLikes, comments, stories, polls, events, actions } from '@shared/schema';
import { eq, and, desc, gte, count, avg, sql } from 'drizzle-orm';

interface NotificationTiming {
  userId: string;
  optimalHours: number[];
  timezone: string;
  averageResponseTime: number;
  lastActiveTime: Date;
}

interface PushNotificationPayload {
  userId: string;
  type: 'like' | 'comment' | 'mention' | 'friend_activity' | 'new_content' | 'conversation_starter';
  title: string;
  body: string;
  data: any;
  priority: 'high' | 'normal' | 'low';
  scheduledFor?: Date;
}

export class NotificationIntelligence {
  /**
   * Analyze user activity patterns to determine optimal notification timing
   */
  async analyzeUserActivityPatterns(userId: string): Promise<NotificationTiming> {
    // Get user's recent activity patterns (last 30 days)
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

    // Analyze when user is most active (posts, likes, comments, opens app)
    const [activityData] = await Promise.all([
      this.getUserActivityHours(userId, thirtyDaysAgo),
    ]);

    // Calculate optimal notification hours based on activity patterns
    const optimalHours = this.calculateOptimalHours(activityData);

    // Get user timezone from profile or default to EST
    const user = await db.select().from(users).where(eq(users.id, userId)).limit(1);
    const timezone = 'America/New_York'; // Default timezone (timezone not in user schema)

    return {
      userId,
      optimalHours,
      timezone,
      averageResponseTime: this.calculateAverageResponseTime(activityData),
      lastActiveTime: activityData.length > 0 ? activityData[0].timestamp : new Date()
    };
  }

  /**
   * Get user activity hours from various interactions
   */
  private async getUserActivityHours(userId: string, since: Date): Promise<Array<{hour: number, timestamp: Date, type: string}>> {
    const activities: Array<{hour: number, timestamp: Date, type: string}> = [];

    // Get posting activity
    const postsData: any[] = await db
      .select({ createdAt: posts.createdAt })
      .from(posts)
      .where(and(eq(posts.userId, userId), gte(posts.createdAt, since)));

    postsData.forEach((post: any) => {
      activities.push({
        hour: post.createdAt.getHours(),
        timestamp: post.createdAt,
        type: 'post'
      });
    });

    // Get liking activity
    const likes = await db
      .select({ createdAt: postLikes.createdAt })
      .from(postLikes)
      .where(and(eq(postLikes.userId, userId), gte(postLikes.createdAt, since)));

    likes.forEach(like => {
      activities.push({
        hour: (like.createdAt || new Date()).getHours(),
        timestamp: like.createdAt || new Date(),
        type: 'like'
      });
    });

    // Get commenting activity
    const commentsData: any[] = await db
      .select({ createdAt: comments.createdAt })
      .from(comments)
      .where(and(eq(comments.userId, userId), gte(comments.createdAt, since)));

    commentsData.forEach((comment: any) => {
      activities.push({
        hour: comment.createdAt.getHours(),
        timestamp: comment.createdAt,
        type: 'comment'
      });
    });

    // Sort by most recent first
    return activities.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  /**
   * Calculate optimal notification hours based on activity patterns
   */
  private calculateOptimalHours(activities: Array<{hour: number, timestamp: Date, type: string}>): number[] {
    if (activities.length === 0) {
      // Default optimal hours if no data: morning, lunch, evening
      return [9, 12, 18, 20];
    }

    // Count activity by hour
    const hourCounts = new Map<number, number>();
    activities.forEach(activity => {
      const current = hourCounts.get(activity.hour) || 0;
      hourCounts.set(activity.hour, current + 1);
    });

    // Find top 4 most active hours
    const sortedHours = Array.from(hourCounts.entries())
      .sort((a, b) => b[1] - a[1])
      .slice(0, 4)
      .map(([hour]) => hour)
      .sort((a, b) => a - b);

    // Ensure we have at least 2 hours, with sensible defaults
    if (sortedHours.length < 2) {
      return [9, 18, ...sortedHours].slice(0, 4);
    }

    return sortedHours;
  }

  /**
   * Calculate average response time for user
   */
  private calculateAverageResponseTime(activities: Array<{hour: number, timestamp: Date, type: string}>): number {
    if (activities.length < 2) return 4; // Default 4 hours

    // Calculate time between activities
    const timeDiffs: number[] = [];
    for (let i = 0; i < activities.length - 1; i++) {
      const diff = activities[i].timestamp.getTime() - activities[i + 1].timestamp.getTime();
      const hours = diff / (1000 * 60 * 60);
      if (hours > 0 && hours < 48) { // Only consider reasonable gaps
        timeDiffs.push(hours);
      }
    }

    if (timeDiffs.length === 0) return 4;

    // Return median response time
    timeDiffs.sort((a, b) => a - b);
    const mid = Math.floor(timeDiffs.length / 2);
    return timeDiffs.length % 2 === 0 
      ? (timeDiffs[mid - 1] + timeDiffs[mid]) / 2 
      : timeDiffs[mid];
  }

  /**
   * Determine if now is a good time to send notification to user
   */
  async shouldSendNotificationNow(userId: string, priority: 'high' | 'normal' | 'low' = 'normal'): Promise<boolean> {
    const timing = await this.analyzeUserActivityPatterns(userId);
    const now = new Date();
    const currentHour = now.getHours();

    // High priority notifications can always be sent (but respect quiet hours)
    if (priority === 'high') {
      return !this.isQuietHour(currentHour);
    }

    // Check if current hour is in optimal hours
    const isOptimalTime = timing.optimalHours.includes(currentHour);

    // Check if user was recently active (within average response time)
    const timeSinceLastActive = (now.getTime() - timing.lastActiveTime.getTime()) / (1000 * 60 * 60);
    const wasRecentlyActive = timeSinceLastActive <= timing.averageResponseTime;

    // Send if optimal time OR recently active (but not both required for normal priority)
    return isOptimalTime || (priority === 'normal' && wasRecentlyActive);
  }

  /**
   * Check if current hour is a quiet hour (late night/early morning)
   */
  private isQuietHour(hour: number): boolean {
    // Quiet hours: 11 PM to 7 AM
    return hour >= 23 || hour <= 7;
  }

  /**
   * Create intelligent push notification with optimal timing
   */
  async createIntelligentNotification(payload: PushNotificationPayload): Promise<void> {
    const timing = await this.analyzeUserActivityPatterns(payload.userId);

    // Determine when to send based on priority and user patterns
    let scheduledFor = new Date();

    if (!await this.shouldSendNotificationNow(payload.userId, payload.priority)) {
      // Schedule for next optimal time
      scheduledFor = this.getNextOptimalTime(timing);
    }

    // Store notification for processing (simplified for schema compatibility)
    try {
      await db.insert(notifications).values({
        userId: payload.userId,
        type: payload.type as any,
        title: payload.title,
        message: payload.body,
        priority: payload.priority as any
      });
    } catch (error) {
      console.error('Failed to store notification:', error);
    }
  }

  /**
   * Get next optimal time for notification
   */
  private getNextOptimalTime(timing: NotificationTiming): Date {
    const now = new Date();
    const currentHour = now.getHours();

    // Find next optimal hour today or tomorrow
    let nextHour = timing.optimalHours.find(hour => hour > currentHour);

    if (!nextHour) {
      // Use first optimal hour tomorrow
      nextHour = timing.optimalHours[0];
      now.setDate(now.getDate() + 1);
    }

    now.setHours(nextHour, 0, 0, 0);
    return now;
  }

  /**
   * Process pending notifications (called by background service)
   */
  async processPendingNotifications(): Promise<void> {
    const now = new Date();

    // Get notifications ready to be sent
    const pendingNotifications = await db
      .select()
      .from(notifications)
      .where(and(
        eq(notifications.isRead, false),
        sql`${notifications.createdAt} <= ${now}`
      ))
      .limit(50);

    for (const notification of pendingNotifications) {
      try {
        // Send push notification (placeholder for actual implementation)
        await this.sendPushNotification(notification);

        // Mark as sent
        await db
          .update(notifications)
          .set({ isRead: true, readAt: new Date() })
          .where(eq(notifications.id, notification.id));

      } catch (error) {
        console.error('Failed to send notification:', error);
      }
    }
  }

  /**
   * Send actual push notification (integrate with FCM/APNS)
   */
  private async sendPushNotification(notification: any): Promise<void> {
    // This would integrate with Firebase Cloud Messaging (Android) and Apple Push Notifications (iOS)
    // For now, this is a placeholder that logs the notification

    console.log(`üì± Smart Notification Sent:`, {
      userId: notification.userId,
      type: notification.type,
      title: notification.title,
      message: notification.message,
      priority: notification.priority,
      scheduledFor: notification.scheduledFor,
      sentAt: new Date()
    });

    // TODO: Implement actual push notification sending
    // Example using Firebase Admin SDK:
    /*
    const message = {
      token: userDeviceToken,
      notification: {
        title: notification.title,
        body: notification.message
      },
      data: JSON.parse(notification.data),
      android: {
        priority: notification.priority === 'high' ? 'high' : 'normal'
      },
      apns: {
        payload: {
          aps: {
            badge: 1,
            sound: 'default'
          }
        }
      }
    };

    await admin.messaging().send(message);
    */
  }

  /**
   * Generate smart notifications for various events
   */
  async generateSmartNotifications(event: {
    type: 'new_post' | 'new_like' | 'new_comment' | 'friend_activity' | 'conversation_starter';
    userId: string;
    targetUserId?: string;
    data: any;
  }): Promise<void> {
    const notifications: PushNotificationPayload[] = [];

    switch (event.type) {
      case 'new_like':
        if (event.targetUserId) {
          notifications.push({
            userId: event.targetUserId,
            type: 'like',
            title: 'üëç New Like!',
            body: `${event.data.liker} liked your post`,
            data: { postId: event.data.postId },
            priority: 'normal'
          });
        }
        break;

      case 'new_comment':
        if (event.targetUserId) {
          notifications.push({
            userId: event.targetUserId,
            type: 'comment',
            title: 'üí¨ New Comment!',
            body: `${event.data.commenter} commented on your post`,
            data: { postId: event.data.postId, commentId: event.data.commentId },
            priority: 'normal'
          });
        }
        break;

      case 'friend_activity':
        // Notify close friends about new posts from high-ranked friends
        const closefriendsToNotify = await this.getCloseFriendsForNotification(event.data.authorId);

        for (const friendId of closefriendsToNotify) {
          notifications.push({
            userId: friendId,
            type: 'friend_activity',
            title: 'üéâ Friend Update!',
            body: `${event.data.authorName} shared something new`,
            data: { postId: event.data.postId },
            priority: 'low'
          });
        }
        break;

      case 'conversation_starter':
        // Notify about posts that are likely to start conversations
        const engagedFriends = await this.getEngagedFriendsForConversation(event.data.postId);

        for (const friendId of engagedFriends) {
          notifications.push({
            userId: friendId,
            type: 'conversation_starter',
            title: 'üó£Ô∏è Join the Conversation!',
            body: `${event.data.authorName} posted something that might interest you`,
            data: { postId: event.data.postId },
            priority: 'low'
          });
        }
        break;
    }

    // Send all generated notifications
    for (const notification of notifications) {
      await this.createIntelligentNotification(notification);
    }
  }

  /**
   * Get close friends who should be notified about activity
   */
  private async getCloseFriendsForNotification(userId: string): Promise<string[]> {
    // Only notify friends ranked 1-5 (closest friends)
    const { friendships } = await import('@shared/schema');

    const closeFriends = await db
      .select({ friendId: friendships.friendId })
      .from(friendships)
      .where(and(
        eq(friendships.userId, userId),
        sql`${friendships.rank} <= 5`
      ));

    return closeFriends.map(f => f.friendId);
  }

  /**
   * Get friends who are likely to engage with conversation-starting content
   */
